allow:
Bash("D:\\_Projects\\ChinoizeStats\\fetch_api.ps1" << 'ENDSCRIPT'
$ErrorActionPreference = "Stop"

Write-Host "=== Step 1: Fetching tournament list ==="
$response = Invoke-RestMethod -Uri "https://play.limitlesstcg.com/api/tournaments?game=OP&limit=200" -Headers @{Accept="application/json"}
$chinoize = @\($response | Where-Object { $_.name -match "chinoize" }\)
Write-Host "Total tournaments returned: $\($response.Count\)"
Write-Host "Chinoize tournaments found: $\($chinoize.Count\)"
Write-Host ""
Write-Host "First 3 Chinoize tournaments:"
$chinoize[0..2] | Select-Object id, name, players, date | Format-Table -AutoSize
Write-Host ""

# Full JSON of first 2 for shape inspection
Write-Host "=== Full JSON of first 2 Chinoize tournaments ==="
$chinoize[0..1] | ConvertTo-Json -Depth 5
Write-Host ""

$tid = $chinoize[0].id
Write-Host "=== Step 2: Using tournament ID: $tid ==="
Write-Host ""

Write-Host "=== Standings \(first 3\) ==="
try {
    $standings = Invoke-RestMethod -Uri "https://play.limitlesstcg.com/api/tournaments/$tid/standings" -Headers @{Accept="application/json"}
    Write-Host "Total standings entries: $\($standings.Count\)"
    @\($standings\)[0..2] | ConvertTo-Json -Depth 5
} catch {
    Write-Host "Error fetching standings: $_"
}
Write-Host ""

Write-Host "=== Pairings \(first 3\) ==="
try {
    $pairings = Invoke-RestMethod -Uri "https://play.limitlesstcg.com/api/tournaments/$tid/pairings" -Headers @{Accept="application/json"}
    Write-Host "Total pairings entries: $\($pairings.Count\)"
    @\($pairings\)[0..2] | ConvertTo-Json -Depth 5
} catch {
    Write-Host "Error fetching pairings: $_"
}
Write-Host ""

Write-Host "=== Tournament Details ==="
try {
    $details = Invoke-RestMethod -Uri "https://play.limitlesstcg.com/api/tournaments/$tid" -Headers @{Accept="application/json"}
    $details | ConvertTo-Json -Depth 5
} catch {
    Write-Host "Error fetching details: $_"
}
ENDSCRIPT)
